import React from "react";
import styled from "styled-components";

const LadderTable = ({ count }) => {
  return (
    <Body count={count}>
      <Column>
        {Array.from({ length: 8 }).map((_, idx) => (
          <Row key={idx}>afjdskl</Row>
        ))}
      </Column>
    </Body>
  );
};

const Ladder = ({ players, legs }) => {
  return (
    <LadderWrapper>
      {Array.from({ length: players.length - 1 }).map((_, idx) => (
        <LadderTable key={idx} count={players.length} />
      ))}
    </LadderWrapper>
  );
};

export default Ladder;

const Body = styled.tbody`
  width: ${({ count }) => `calc(100% / ${count})`};
  background-color: skyblue;
`;

const Column = styled.tr`
  background-color: seagreen;
  width: inherit;
`;

const Row = styled.td`
  /* width: 0.8rem;
  height: 40rem;
  margin: 1rem 0; */
  background-color: blue;
  width: inherit;
  height: 3rem;

  @media ${({ theme }) => theme.mobile} {
    width: 0.5rem;
    height: 10rem;
  }
`;

const LadderWrapper = styled.table`
  width: 80%;
  height: 42rem;
  margin: 2rem auto;
  display: flex;
  /* flex-direction: column; */
  justify-content: center;
  background-color: pink;

  @media ${({ theme }) => theme.mobile} {
    width: 100%;
    height: 12rem;
    margin: 1rem auto;
  }
`;
