import React from "react";
import styled from "styled-components";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import {
  faArrowAltCircleLeft,
  faArrowAltCircleRight,
} from "@fortawesome/free-solid-svg-icons";

const CounterButton = ({ direction, increasePlayers, decreasePlayers }) => {
  const clickEvent = direction === "left" ? decreasePlayers : increasePlayers;
  const label = `플레이어 수 ${direction === "left" ? "감소" : "증가"}`;
  const icon =
    direction === "left" ? faArrowAltCircleLeft : faArrowAltCircleRight;

  return (
    <Button aria-label={label}>
      <FontAwesomeIcon icon={icon} color={"orange"} />
    </Button>
  );
};

export default CounterButton;

const Button = styled.button`
  font-size: 4rem;
`;
