/* eslint-disable react-hooks/exhaustive-deps */
import Paths from "Components/Paths";
import React, {
  useRef,
  useEffect,
  useCallback,
  useContext,
  useState,
} from "react";
import { Context } from "Context";

const PathsContainer = ({ idx, canvasRef }) => {
  const { state } = useContext(Context);
  const { players, playerCount } = state;
  const [canvas, setCanvas] = useState(null);

  const ctx = canvas && canvas.getContext("2d");
  const canvasWidth = canvas && canvas.offsetWidth;
  const canvasHeight = canvas && canvas.offsetHeight;
  // let viewPort = window.innerWidth > 812 ? "pc" : "mobile";
  let gapX = canvasWidth / (playerCount * 2);
  let gapY = canvasHeight / 10;
  let posX = gapX * (idx + 1);
  let posY = 2;

  console.log(id, canvasWidth, canvasHeight, gapX, gapY);

  const drawPath = () => {
    ctx.beginPath();
    ctx.arc(posX, posY, 2.5, 0, Math.PI * 2);
    ctx.fillStyle = players[idx].color;
    ctx.fill();
    ctx.closePath();
  };

  useEffect(() => {
    setCanvas(canvasRef.current);
    canvas && drawPath();
  }, [canvasRef, canvas]);

  return <Paths />;
};

export default PathsContainer;
