import React from "react";
import styled from "styled-components";

const Partition = ({ gameState }) => {
  return (
    <>
      <Shade>
        {gameState === "setting" ? (
          <SettingMessage>케이스를 입력해주세요</SettingMessage>
        ) : (
          <GoMessage>시작</GoMessage>
        )}
      </Shade>
    </>
  );
};

export default Partition;

const GoMessage = styled.button`
  font-size: 5rem;
  font-weight: 600;
  color: white;
  background-color: orange;
  /* width: 15rem;
  height: 15rem;
  border-radius: 50%; */
`;

const SettingMessage = styled.h2`
  font-size: 2.5rem;
  color: darkslategrey;

  @media ${({ theme }) => theme.mobile} {
    font-size: 1.5rem;
  }
`;

const Shade = styled.div`
  width: 80%;
  height: 42rem;
  margin: 2rem auto;
  display: flex;
  justify-content: center;
  align-items: center;
  background-color: lightgoldenrodyellow;

  @media ${({ theme }) => theme.mobile} {
    height: 12rem;
    margin: 1rem auto;
  }
`;
