/* eslint-disable react-hooks/exhaustive-deps */
import Paths from "Components/Paths";
import React, {
  useRef,
  useEffect,
  useCallback,
  useContext,
  useState,
} from "react";
import { Context } from "Context";

const PathsContainer = ({ idx }) => {
  const { state } = useContext(Context);
  const { players } = state;

  let canvasRef = useRef(null);
  let canvas = null;
  let ctx = null;

  const drawBall = () => {
    ctx.beginPath();
    ctx.arc(1, 2, 2, 0, Math.PI * 2);
    ctx.fillStyle = players[0].color;
    ctx.fill();
    ctx.closePath();
  };

  useEffect(() => {
    canvas = canvasRef.current;
    ctx = canvas && canvas.getContext("2d");
  }, []);

  return <Paths ref={canvasRef} />;
};

export default PathsContainer;
