import React, { forwardRef, useEffect } from "react";
import styled from "styled-components";

const Paths = forwardRef((props, ref) => {
  const { player, playerCount } = props;
  return <Canvas ref={ref} player={player} playerCount={playerCount}></Canvas>;
});

export default Paths;

const Canvas = styled.canvas`
  width: ${({ player, playerCount }) =>
    `cal(100% / ${playerCount} * ${player})`};
  height: 100%;
  z-index: 1000;
  background-color: lightblue;
`;
