/* eslint-disable react-hooks/exhaustive-deps */
import Paths from "Components/Paths";
import React, {
  useRef,
  useEffect,
  useCallback,
  useContext,
  useState,
} from "react";
import { Context } from "Context";

const PathsContainer = ({ idx, canvasRef }) => {
  const { state, getResult } = useContext(Context);
  const { legs, players, playerCount } = state;
  const [canvas, setCanvas] = useState(null);
  let draw = null;
  let posX = idx;
  let posY = 0;
  let isCrossing = false;

  const ctx = canvas && canvas.getContext("2d");
  const canvasWidth = canvas && canvas.width;
  const canvasHeight = canvas && canvas.height;
  const viewPort = window.innerWidth > 812 ? "pc" : "mobile";
  const radius = viewPort === "pc" ? 2 : 1.5;
  const gapY = canvasHeight / 10;
  const gapX = canvasWidth / (playerCount * 2);
  const RIGHT = "RIGHT";
  const LEFT = "LEFT";
  const STRAIGHT = "STRAIGHT";

  let coordY = 0;

  const getNewX = (posX) => {
    const newX =
      posX === 0
        ? gapX * (2 * idx + 1) + 1.5
        : idx === playerCount - 1
        ? gapX * (2 * idx + 1) - 1.5
        : gapX * (2 * idx + 1);

    return newX;
  };

  let coordX = getNewX(posX);

  const drawFootprint = (X, Y) => {
    ctx.beginPath();
    ctx.arc(X, Y, radius, 0, Math.PI * 10);
    ctx.fillStyle = players[idx].color;
    ctx.fill();
    ctx.closePath();
  };

  const crossLeg = (direction) => {
    console.log("is crossing");
    const newX = direction === RIGHT ? coordX + 2 : coordX - 2;
    const finalX = getNewX(posX);
    console.log(posX, newX, finalX);
    drawFootprint(newX, coordY);
    if (newX >= finalX) {
      isCrossing = false;
      coordY += 1;
      console.log("finish crossing");
    }
  };

  const drawPath = () => {
    if (coordY === canvasHeight) {
      clearInterval(draw);
      getResult(idx, posX);
      return;
    }

    const needChecking = coordY !== 0 && coordY % gapY === 0;
    let direction = STRAIGHT;

    console.log("is drawing ==========", legs);
    // console.log(needChecking, coordY, gapY, canvasHeight, coordY % gapY);

    if (needChecking) {
      const isRight = (legs[posX] && legs[posX].includes(posY)) || false;
      const isLeft = (legs[posX - 1] && legs[posX - 1].includes(posY)) || false;
      direction = isRight ? RIGHT : isLeft ? LEFT : STRAIGHT;

      console.log("is checking ===============");
      console.log(coordY, posY, isLeft, isRight, direction);
      // console.log(idx, posX, direction, posY);

      posY++;
      posX = isRight ? posX + 1 : isLeft ? posX - 1 : posX;
      isCrossing = direction !== STRAIGHT;
    }

    if (isCrossing) {
      // console.log("is crossing =============");
      // console.log("인덱스", idx, "posX, posY", posX, posY);
      crossLeg(direction);
    }

    if (!isCrossing) {
      coordY += 1;
      drawFootprint(coordX, coordY);
    }
  };

  useEffect(() => {
    setCanvas(canvasRef.current);
    if (canvas) draw = setInterval(() => drawPath(), 100);
  }, [canvasRef, canvas]);

  return <Paths />;
};

export default PathsContainer;
