/* eslint-disable react-hooks/exhaustive-deps */
import Paths from "Components/Paths";
import React, {
  useRef,
  useEffect,
  useCallback,
  useContext,
  useState,
} from "react";
import { Context } from "Context";

const PathsContainer = ({ idx, canvasRef }) => {
  const { state, getResult } = useContext(Context);
  const { legs, players, playerCount } = state;
  const [canvas, setCanvas] = useState(null);
  let draw = null;
  let posX = idx;
  let posY = 1;
  let isCrossing = false;
  let result = null;

  const ctx = canvas && canvas.getContext("2d");
  const canvasWidth = canvas && canvas.width;
  const canvasHeight = canvas && canvas.height;
  const viewPort = window.innerWidth > 812 ? "pc" : "mobile";
  const radius = viewPort === "pc" ? 2 : 1.5;
  const gapY = canvasHeight / 10;
  const gapX = canvasWidth / (playerCount * 2);
  let coordY = 0;
  let coordX =
    posX === 0
      ? gapX * (2 * idx + 1) + 1.5
      : posX === playerCount - 1
      ? gapX * (2 * idx + 1) - 1.5
      : gapX * (2 * idx + 1);

  const drawFootprint = () => {
    ctx.beginPath();
    ctx.arc(coordX, coordY, radius, 0, Math.PI * 10);
    ctx.fillStyle = players[idx].color;
    ctx.fill();
    ctx.closePath();
  };

  const crossLeg = (direction) => {};

  const drawPath = () => {
    if (coordY === canvasHeight) {
      clearInterval(draw);
      getResult(idx, result);
      return;
    }

    const needChecking = !isCrossing && !(coordY % gapY);
    let direction = "sraight";

    if (needChecking) {
      const isRight = legs[posX] && legs[posX].includes(posY);
      const isLeft = legs[posX - 1] && legs[posX - 1].includes(posY);
      direction = isRight ? "right" : isLeft ? "left" : "straight";

      console.log(isLeft, isRight);
      console.log(idx, posX, direction, posY);
      posY++;
    }

    if (isCrossing) {
      crossLeg(direction);
    }

    if (!isCrossing) coordY += radius;

    drawFootprint();
  };

  useEffect(() => {
    setCanvas(canvasRef.current);
    if (canvas) draw = setInterval(() => drawPath(), 30);
  }, [canvasRef, canvas]);

  return <Paths />;
};

export default PathsContainer;
