export const initState = {
  page: "home",
  playerCount: 2,
  players: [],
  cases: {},
  results: [],
  gameState: "ready",
  ladder: [],
  legs: [],
};

export const reducer = (action, state) => {
  switch (action) {
    case "INCREASE_PLAYERS":
      return {
        ...state,
        playerCount: state.playerCount + 1,
      };
    case "DECREASE_PLAYERS":
      return {
        ...state,
        playerCount: state.playerCount - 1,
      };
    default:
      throw new Error("Unhandled action type");
  }
};
