import Case from "Components/CaseList";
import React, { useContext, useEffect } from "react";
import { Context } from "Context";
import CaseList from "Components/CaseList";

const CaseListContainer = () => {
  const { state, isReady, resetCase, inputCase } = useContext(Context);
  const { players, playerCount, page, cases, gameState } = state;

  useEffect(() => {
    resetCase(playerCount);
  }, []);

  return (
    <CaseList
      players={players}
      playerCount={playerCount}
      cases={cases}
      gameState={gameState}
      isReady={isReady}
      inputCase={inputCase}
    />
  );
};

export default CaseListContainer;
