    <Wrapper>
      {page !== "result" && <SubHeader className="a11y-hidden">{}</SubHeader>}
      {children}
    </Wrapper>
  );
};

export default Main;

const Wrapper = styled.main`
  width: 100vw;
  position: relative;
`;

const SubHeader = styled.h2``;
