import Main from "Components/Main";
import React from "react";

const MainContainer = ({ children }) => {
  const { state } = useContext(Context);
  const { page } = state;

  return (
    <Main>
      {page === "home" ? <Home /> : page === "game" ? <Game /> : <Result />}
    </Main>
  );
};

export default MainContainer;
